import java.util.*;public class c {	static int n, arr[];	public static void main(String[] args) {		Scanner in = new Scanner(System.in);		int T = in.nextInt();		for(int t=1; t<=T; t++) {			System.out.printf("Case #%d: ", t);			n = in.nextInt();			arr = new int[n];			int sum = 0;			int realsum = 0;			int min = (int)1e9;			for(int i=0; i<n; i++) {				arr[i] = in.nextInt();				sum ^= arr[i];				realsum += arr[i];				min = Math.min(min,arr[i]);			}			if(sum != 0) {				System.out.println("NO");				continue;			}			realsum -= min;			System.out.println(realsum);		}	}}