import java.util.*;public class d {	static double[] memo = new double[1010];	static double[][] memoo = new double[1010][1010];	public static void main(String[] args) {		Scanner in = new Scanner(System.in);		int T = in.nextInt();		for(int t=1; t<=T; t++) {			int n = in.nextInt();			int k =0;			for(int i=1; i<=n; i++) 				if(i != in.nextInt())					k++;			System.out.printf("Case #%d: %f%n", t,(double)k);		}	}	public static double f(int k, int j) {		if(j<0 || j>k) return 0;		if(k==0 && j==0) return 1;		if(memoo[k][j] != 0) return memoo[k][j];		double ans = f(k-1,j-1) + f(k-1,j)*(k-1-j) + f(k-1,j+1)*(j+1);		ans /= k;		return memoo[k][j] = ans;	}	public static double go(int k) {		if(k==0) return 0;		if(memo[k] != 0) return memo[k];		double total = 1;		for(int i=1; i<=k; i++) {			total += f(k,i)*go(k-i);		}		total /= (1-f(k,0));		return memo[k] = total;	}}