import java.util.*;public class a {	public static void main(String[] args) {		Scanner in = new Scanner(System.in);		int T = in.nextInt();		for(int t=1; t<=T; t++) {			int n = in.nextInt();			Pair[] pairs = new Pair[n];			int[] v1 = new int[n];			int[] v2 = new int[n];			for(int i=0; i<n; i++)				v1[i] = in.nextInt();			for(int i=0; i<n; i++)				v2[i] = in.nextInt();			for(int i=0; i<n; i++)				pairs[i] = new Pair(i,v2[i],v1[i]);			Arrays.sort(pairs);			System.out.printf("Case #%d:", t);			for(Pair p : pairs)				System.out.print(" " + p.id);			System.out.println();		}	}	static class Pair implements Comparable<Pair> {		int time, prob, id;		Pair(int a, int b, int c) {			id = a;			prob = b;			time = c;		}		public int compareTo(Pair p) {			if(prob*p.time != time*p.prob)				return time*p.prob-prob*p.time;			return id-p.id;		}	}}